#include "APBAnimation.h"APBAnimation::APBAnimation() {    currentFrame = 0;    maxFrames = 0;    frameInc = 1;    frameRate = 80; // 12,5 fps    frameRate = 40; // 25 fps    oldTime = 0;    oscillate = false;    loop = true;}void APBAnimation::animate() {    if (oldTime + frameRate > SDL_GetTicks()) {        return;    }    oldTime = SDL_GetTicks();    currentFrame += frameInc;    if (oscillate) {        if (frameInc > 0) {            if (loop && currentFrame >= maxFrames -1 ) {                frameInc = -frameInc;            }        } else {            if (loop && currentFrame <= 0) {                frameInc = -frameInc;            }        }    } else {        if (loop && currentFrame >= maxFrames) {            currentFrame = 0;        }    }}void APBAnimation::setFrameRate(int rate) {    frameRate = rate;}void APBAnimation::setCurrentFrame(int frame) {    if (frame < 0 || frame >= maxFrames) return;    currentFrame = frame;}int APBAnimation::getCurrentFrame() {    return currentFrame;}