#include "APBSurface.h"APBSurface::APBSurface() {}SDL_Surface* APBSurface::load(char* File) {    SDL_Surface* tmp = NULL;    SDL_Surface* surface = NULL;    if ((tmp = IMG_Load(File)) == NULL) {        return NULL;    }    surface = SDL_DisplayFormatAlpha(tmp);    SDL_FreeSurface(tmp);    return surface;}bool APBSurface::draw(SDL_Surface* dst, SDL_Surface* src, int x, int y) {    if (dst == NULL || src == NULL) {        return false;    }    SDL_Rect dstRect;    dstRect.x = x;    dstRect.y = y;    SDL_BlitSurface(src, NULL, dst, &dstRect);    return true;}bool APBSurface::draw(SDL_Surface* dst, SDL_Surface* src, int x1, int y1, int x2, int y2, int w, int h) {    if (dst == NULL || src == NULL) {        return false;    }    SDL_Rect dstRect;    dstRect.x = x1;    dstRect.y = y1;    SDL_Rect srcRect;    srcRect.x = x2;    srcRect.y = y2;    srcRect.w = w;    srcRect.h = h;    SDL_BlitSurface(src, &srcRect, dst, &dstRect);    return true;}bool APBSurface::setTransparentColor(SDL_Surface* dst, int r, int g, int b) {    if (dst == NULL) {        return false;    }    return SDL_SetColorKey(dst, SDL_SRCCOLORKEY|SDL_RLEACCEL, SDL_MapRGB(dst->format, r, g, b));}