#include "TestGame.h"int main( int argc, char* argv[] ) {    TestGame game;    game.setDisplayProperties( 800, 480, 16 );    return game.execute();}TestGame::TestGame() {    surfTest = NULL;}bool TestGame::loadData() {    if ((surfTest = APBSurface::load("data/graphics/numbertest_small.png")) == NULL) {        fprintf(stderr,"Failed to load data files");        return false;    }    NumberTestSprite.maxFrames = surfTest->h/surfTest->w;    NumberTestSprite.oscillate = true;    return true;}void TestGame::render() {    SDL_FillRect( APBApp::display, NULL, 0 );    for (int j = 0; j < APBApp::display->h; j+=surfTest->w) {        for (int i = 0; i < APBApp::display->w; i+=surfTest->w) {            APBSurface::draw(APBApp::display, surfTest, i, j, 0, NumberTestSprite.getCurrentFrame() * surfTest->w, surfTest->w, surfTest->w);        }    }    SDL_Flip(APBApp::display);}void TestGame::cleanup() {    SDL_FreeSurface(surfTest);    APBApp::cleanup();}void TestGame::loop() {    NumberTestSprite.animate();}